
public class multifor {

	/*
	 Цикл мультифор
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		 for(int x=0;x<4;x++){
			 
			 for(int y=4;y>2;y--){
				 if (x==1){				 
					 x++;
			      }	 
			 // выход из ветвления иф 
			 System.out.println(x+" "+y);
		 }// выход из с игриком
	   } // выход из цикла и иксом
	}// выход из главного метода

}// выход из класса
